mapper.select:
SELECT POST_TYPE_CODE,SUG_NO,TITLE,USER_ID,WRITE_DATE,INQ_COUNT,PUSH_COUNT,CON,RNUM,REQ_NO FROM( SELECT POST_TYPE_CODE,SUG_NO,TITLE,USER_ID, WRITE_DATE,INQ_COUNT,PUSH_COUNT,CON,ROW_NUMBER() OVER(PARTITION BY POST_TYPE_CODE ORDER BY ANO DESC) AS RNUM,REQ_NO FROM( SELECT POST_TYPE_CODE,SUG_NO, TITLE, USER_ID, TO_CHAR(WRITE_DATE, 'YYYY/MM/DD HH24:MI:SS')AS WRITE_DATE,INQ_COUNT,PUSH_COUNT,CON,REQ_NO, CASE WHEN REQ_NO !=0 THEN REQ_NO -1 ELSE SUG_NO END ANO FROM SUG WHERE 1=1 AND DEL_STATUS=1
AND TITLE LIKE '%' || #{searchTxt} || '%'
AND USER_ID LIKE '%' || #{searchTxt} || '%'
AND CON LIKE '%' || #{searchTxt} || '%'
)) WHERE RNUM BETWEEN #{startCnt} AND #{endCnt}
